import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import CustomerService from '../services/CustomerService';
import {Dropdown} from "bootstrap";
import { Button } from 'bootstrap';

class NavbarComponent extends Component {
    constructor(props){
        console.log(props)
        super(props)
        this.state={
            username:"",
            password:"",
            passwordReplacement:"",
            userId:"",
            customers:[],
            isLoggedIn:false,
            user:"",
            isEmployee:false
        }
    }
    componentDidMount(){
        CustomerService.getCustomers().then((res)=>{
            this.setState({
                customers:res.data
            })
        })
    }
    changeUsernameHandler=(e)=>{
        this.setState({
            username:e.target.value
        })
    }
    // changePassword=(e)=>{
    //     let passwordReplacement= ""
    //     for(let i = 0; i<e.target.value.length;i++){
    //         passwordReplacement+="*"
    //     }
    //     let addition= e.target.value.slice(e.target.value.length-1)
    //     let password=this.state.password+addition
    //     console.log(password)
    //     this.setState({
    //         password:password,
    //         passwordReplacement: passwordReplacement
    //     })
    // }
    changePassword=(e)=>{
        this.setState({
            password:e.target.value
        })
    }
    logIn=(e)=>{
        e.preventDefault()

        let user = this.state.customers.filter((customer)=> customer.email===this.state.username)
        console.log(user)
        if(user[0] && this.state.password===user[0].password){
            this.setState({
                isLoggedIn:true,
                user:user,
                customers:"",
                isEmployee:user[0].employee
            })
           this.props.loggedIn(user[0])
        }

    }


    render() {
        return (
            <div>
                <header>
                    <nav className="navbar navbar-expand-md navbar-dark bg-dark">

                    <div><h3 className='navbar-brand'>Flight Management</h3></div>

                    <Link to={{pathname:"/flights" , params:{user:this.state.user[0]}}}className='nav-link'>Flights</Link>
                    <div className='navbar-right'>
                   <Link to={"/add-customer"}className='nav-link navbar-right'>Register</Link>
                   <Link to={"/sign-in"} className='nav-link navbar-right'>Sign In</Link>
                    </div>

                    <div></div>
                    </nav>
                </header>
            </div>
        );
    }
}

export default NavbarComponent;
