import React from "react";
import { Link } from "react-router-dom";
import { useState } from "react";
import ReservationService from "../../services/ReservationService";
function flightBooker(flights, props) {
  let user = JSON.parse(sessionStorage.getItem("user"));
  flights.forEach((flight) => {
    ReservationService.createReservations(flight.id, user.id).then(() => {});
  });

  props.history.push(`/view-customer/${user.id}`);
}
function ItineraryComponent(props) {
  const [flights, setFlights] = useState(props.itinerary);
  console.log(props.itinerary);
  console.log(flights);

  return (
    <div className="container">
      <div className="card m-5 bg-subtle shadow-lg">
        <h2 className="text-center mt-3 ">Selected Itinerary</h2>
        <div className="row flight-list"> </div>
        <table className="table  table-striped flight-list mb-5">
          <thead>
            <tr>
              <th>Airline</th>
              <th>Departure Time</th>
              <th>Arrival Time</th>
              <th>Details</th>
            </tr>
          </thead>
          <tbody className="flight-list">
            {flights.map((flight) => (
              <tr key={flight.id}>
                <td>{flight.airline}</td>
                <td>
                  <small>
                    {new Date(flight.departureDate).toLocaleString()}
                  </small>
                </td>
                <td>
                  <small>{new Date(flight.arrivalDate).toLocaleString()}</small>
                </td>
                <td>
                  {/* <Link
                  className="btn btn-light"
                  to={`/view-flight/${flight.id}`}
                >
                  View
                </Link>*/}
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="row">
          <button
            className="btn btn-dark col-2 offset-md-9 mb-2"
            onclick={flightBooker(flights, props)}
          >
            Book
          </button>
        </div>
      </div>
    </div>
  );
}

export default ItineraryComponent;
