import React, { Component } from "react";
class DynamicSelect extends Component {
  constructor(props) {
    super(props);
  }
  //On the change event for the select box pass the selected value back to the parent
  handleChange = (event) => {
    let selectedValue = event.target.value;
    this.props.onSelectChange(selectedValue);
  };
  render() {
    let arrayOfData = this.props.arrayOfData;
    let options = arrayOfData.map((data) => (
      <option key={data.id} value={data.id}>
        {" "}
        {data.name}{" "}
      </option>
    ));
    return (
      <select
        name="customSearch"
        className="custom-search-select"
        onChange={this.handleChange}
      >
        <option>Select Item</option> {options}{" "}
      </select>
    );
  }
}

const arrayOfData = [
  { id: "1 - Jerry", name: "Jerry" },
  { id: "2 - Elaine", name: "Elaine" },
  { id: "3 - Kramer", name: "Kramer" },
  { id: "4 - George", name: "George" },
];
class App extends Component {
  constructor(props) {
    super(props);
    this.state = { selectedValue: "Nothing selected" };
  }
  handleSelectChange = (selectedValue) => {
    this.setState({ selectedValue: selectedValue });
  };
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img className="App-logo" alt="logo" />
        </header>{" "}
        <p className="App-intro">
          <DynamicSelect
            arrayOfData={arrayOfData}
            onSelectChange={this.handleSelectChange}
          />{" "}
          <br />
          <br />
          <div> Selected value: {this.state.selectedValue} </div>{" "}
        </p>{" "}
      </div>
    );
  }
}

export default App;
