import axios from "axios";

const AUTH_API_URL="http://localhost:8080/api/auth/";

class AuthService{
    //logs in by submitting the username and password to the bvackend, if it receives a token back it knows the user is correct and stores it in local storage so the user is persistently logged in
    login(username, password){
        axios.post(AUTH_API_URL+"sign-in",{username,password}).then(response =>{
            console.log()
            if(response.data.accessToken){
                localStorage.setItem("user", JSON.stringify(response.data))
            }
            return response.data;
        })
    }
    //on logout it removes the user from local storage
    logout(){
        localStorage.removeItem("user")
    }
    //submits customer data to sign up to register user
    signup(customer){
        axios.post(AUTH_API_URL+"signup", customer)
    }
    //gets the user info for thhe user currently logged in.
    getCurrentUser(){
        return JSON.parse(localStorage.getItem('user'))
    }
}

export default new AuthService();
