import React, { Component } from "react";
import FlightService from "../../services/FlightService";
import FlightSearchComponent from "./FlightSearchComponent";

class ViewFlightComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: this.props.match.params.id || 0,
      flight: {},
    };
  }
  componentDidMount() {
    FlightService.getFlightById(this.state.id).then((res) => {
      this.setState({
        flight: res.data,
      });
    });
  }
  render() {
    let flight = this.state.flight;
    return <div>{flight}</div>;
  }
}

export default ViewFlightComponent;
