import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import CustomerService from '../services/CustomerService';
import {Dropdown} from "bootstrap";
import { Button } from 'bootstrap';

class HeaderComponent extends Component {
    constructor(props){
        super(props)
        this.state={
            username:"",
            password:"",
            passwordReplacement:"",
            userId:"",
            customers:[]
        }
    }
    componentDidMount(){
        CustomerService.getCustomers().then((res)=>{
            this.setState({
                customers:res.data
            })
        })
    }
    changeUsernameHandler=(e)=>{
        this.setState({
            username:e.target.value
        })
    }
    // changePassword=(e)=>{
    //     let passwordReplacement= ""
    //     for(let i = 0; i<e.target.value.length;i++){
    //         passwordReplacement+="*"
    //     }
    //     let addition= e.target.value.slice(e.target.value.length-1)
    //     let password=this.state.password+addition
    //     console.log(password)
    //     this.setState({
    //         password:password,
    //         passwordReplacement: passwordReplacement
    //     })
    // }
    changePassword=(e)=>{
        this.setState({
            password:e.target.value
        })
    }
    logIn=(e)=>{
        e.preventDefault()

        let customer = this.state.customers.filter((customer)=> customer.email===this.state.username)
        console.log(customer)
        if(customer[0] && this.state.password===customer[0].password){
            console.log(this.props)
        }
    }


    render() {
        return (
            <div>
                <header>
                    <nav className="navbar navbar-expand-md navbar-dark bg-dark">

                    <div><a href="http://localhost:3000/" className='navbar-brand'>Flight Management</a></div>
                     <Link to="/customers">View All Customers</Link>
                    <Link to="/flights">Flights</Link>
                    <Link to="/add-airport">Add airport</Link>
                    <div className="dropdown">
                    <button className="btn btn-secondary dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">
                    Dropdown button
                    </button>
                    <ul className="dropdown-menu dropdown-menu-dark" ><li>
                       <form>
                       <label>Username(Email):</label>
                             <input placeholder='Username' name='username' className='form-control' value={this.state.username} onChange={this.changeUsernameHandler}></input>
                        <label>Password:</label>
                            <input placeholder='Password' name='password' className='form-control' value={this.state.password} onChange={this.changePassword}></input>
                        <button className='btn btn-primary' onClick={this.logIn}>Log In</button> <Link className="btn btn-primary"to="/add-customer">Sign Up</Link>

                       </form>
                    </li>

                    </ul>
                    </div>

                    <div></div>
                    </nav>
                </header>
            </div>
        );
    }
}

export default HeaderComponent;
