import React, { Component } from 'react';
import {Link} from 'react-router-dom'
import CustomerService from '../../services/CustomerService';






class CreateCustomerComponent extends Component {

    constructor(){
        super()
        this.state={
            firstName:"",
            middleName:"",
            lastName:"",
            email:"",
            address:"",
            phoneNumber:"",
            city:"",
            state:"",
            country:"",
            employee:false,
            password: ""
        }

        this.changeEmailHandler = this.changeEmailHandler.bind(this)
        this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this)
        this.changeLastNameHandler =this.changeLastNameHandler.bind(this)
        this.saveCustomer = this.saveCustomer.bind(this)
        this.changeMiddleNameHandler =this.changeMiddleNameHandler.bind(this)
        this.changeAddressHandler = this.changeAddressHandler.bind(this)
        this.changePhoneNumberHandler = this.changePhoneNumberHandler.bind(this)
        this.changeCityHandler = this.changeCityHandler.bind(this)
        this.changeStateHandler = this.changeStateHandler.bind(this)
        this.changeCountryHandler = this.changeCountryHandler.bind(this)
        this.changePasswordHandler = this.changePasswordHandler.bind(this)



    }
    changeFirstNameHandler = (event) =>{
        this.setState({
            firstName:event.target.value
        })

    }
    changeLastNameHandler = (event) =>{
        this.setState({
            lastName:event.target.value
        })

    }
    changeEmailHandler = (event) =>{
        this.setState({
            email:event.target.value
        })
    }
    changeMiddleNameHandler = (event) =>{
        this.setState({
            middleName:event.target.value
        })
    }
    changePhoneNumberHandler = (event) =>{
        this.setState({
             phoneNumber:event.target.value
        })
    }        
    changeAddressHandler = (event) =>{
        this.setState({
            address:event.target.value
        })
    }
    changeCityHandler = (event) =>{
        this.setState({
            city:event.target.value
        })
    }
    changeStateHandler = (event) =>{
        this.setState({
            state:event.target.value
        })
    }
    changeCountryHandler = (event) =>{
        this.setState({
            country:event.target.value
        })
    }
    changePasswordHandler=(event)=>{
        this.setState({
            password:event.target.value
        })
    }
    saveCustomer= (e) => {

        e.preventDefault()
        let customer = {firstName: this.state.firstName, lastName: this.state.lastName, email: this.state.email, middleName:this.state.middleName,phoneNumber:this.state.phoneNumber,address:this.state.address,city:this.state.city,state:this.state.state,country:this.state.country, employee:this.state.employee, password:this.state.password}
        if(customer.firstName!==''&&customer.lastName!==''&&customer.email!==''){

        CustomerService.createCustomer(customer).then(()=>{
            this.props.history.push("/customers")
        })
    }
    }

    render() {

        return (

            <div className='container'>
            <div className='row'>
                <div className='card col-md-6 offset-md-3 offset-md-3'>
                    <h3 className='text-center'>Sign Up</h3>
                    <div className='card-body'>
                        <form>
                            <div className='form-group'>
                                <label>First Name:</label>
                                <input placeholder='First Name' name='firstName' className='form-control' value={this.state.firstName} onChange={this.changeFirstNameHandler}></input>
                                <label>Middle Name:</label>
                                <input placeholder='Middle Name' name='middleName' className='form-control'value={this.state.middleName} onChange={this.changeMiddleNameHandler}></input>
                                <label>Last Name:</label>
                                <input placeholder='Last Name' name='lastName' className='form-control'value={this.state.lastName} onChange={this.changeLastNameHandler}></input>
                                <label>Email:</label>
                                <input placeholder='Email' name='email' className='form-control'value={this.state.email} onChange={this.changeEmailHandler}></input>
                                <label>Phone Number:</label>
                                <input placeholder='Phone Number' name='phoneNumber' className='form-control'value={this.state.phoneNumber} onChange={this.changePhoneNumberHandler}></input>
                                <label>Address:</label>
                                <input placeholder='Address' name='address' className='form-control'value={this.state.address} onChange={this.changeAddressHandler}></input>
                                <label>City:</label>
                                <input placeholder='City' name='city' className='form-control'value={this.state.city} onChange={this.changeCityHandler}></input>
                                <label>State:</label>
                                <input placeholder='State' name='state' className='form-control'value={this.state.state} onChange={this.changeStateHandler}></input>
                                <label>Country:</label>
                                <input placeholder='Country' name='country' className='form-control'value={this.state.country} onChange={this.changeCountryHandler}></input>
                                <label>Password:</label>
                                <input placeholder='Password' name='password' className='form-control'value={this.state.password} onChange={this.changePasswordHandler}></input>
                            </div>
                            <button className='btn btn-success' onClick={this.saveCustomer}>Submit</button>
                            <Link className='btn btn-danger' style={{marginLeft:"10px"}}to="/customers">Cancel</Link>
                        </form>
                    </div>


                </div>
            </div>
            </div>

        );
    }
}

export default CreateCustomerComponent;
