import React, { Component } from "react";
import AirportService from "../../services/AirportService";

class FlightSearchComponent extends Component {
  constructor() {
    super();
    this.state = {
      airports: [],
      selectCity: "None",
    };
  }
  componentDidMount() {
    AirportService.getAirports().then((res) => {
      this.setState({
        airports: res.data,
      });
    });
  }
  render() {
    let airports = this.state.airports;
    return (
      <div>
        <div className="row">
          <div className="card col-md-6 offet-md-3 offset-md-3">
            <h2 className="text-center">Book Flights</h2>
            <div className="card-body">
              <form>
                <div className="form-group">
                  <label>Origin:</label>
                  <select
                    className="mdb-select md-form"
                    searchable="Search here.."
                  >
                    <option value={this.state.selectCity} disabled selected>
                      Choose your country
                    </option>
                    {airports.map((airport) => (
                      <option value={airport.code}>{airport.location}</option>
                    ))}
                  </select>
                  <label>Destination:</label>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default FlightSearchComponent;
