import React, { Component } from "react";
import CustomerService from "../../services/CustomerService";
import ReservationService from "../../services/ReservationService";

class ViewCustomerComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: this.props.match.params.id || 0,
      customer: {},
      reservations: {},
    };
  }
  componentDidMount() {
    CustomerService.getCustomerById(this.state.id).then((res) => {
      this.setState({
        customer: res.data,
      });
    });
    ReservationService.getReservationForCustomer(this.state.id).then((res) => {
      console.log(this.state.id, res);
      this.setState({
        reservations: res.data,
      });
    });
  }
  render() {
    let customer = this.state.customer;
    return (
      <div>
        <div className=" card ">
          <h3 className="text-center">View Customer Details</h3>
          <div className="cardbody">
            <div className="row">
              <label>Customer Name:</label>
              <div>{`${customer.firstName} ${customer.middleName} ${customer.lastName}`}</div>
              <label>Customer Email:</label>
              <div>{customer.email}</div>
              <label>Customer Phone Number:</label>
              <div>{customer.phoneNumber}</div>
              <label>Customer Adress:</label>
              <div>{`${customer.address} ${customer.city} , ${customer.state} , ${customer.country}`}</div>
              <label>Flights:</label>
              {this.reservations[0] ? <div>true</div> : <div>false</div>}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ViewCustomerComponent;
