import React, { Component } from 'react';
import AirportService from '../../services/AirportService';

class CreateAirportComponent extends Component {
    constructor(props){
        super(props)
        this.state={
            airportName:"",
            airportCode:"",
            airportLocation:""
        }
    }
    changeAirportName= (e) => {
        this.setState({
            airportName: e.target.value
        })
    }
    changeAirportCode =(e)=>{
        if(e.target.value.length<=3){
        this.setState({
            airportCode: e.target.value
        })
    }
    }
    changeAirportLocation=(e)=>{
        this.setState({
            airportLocation:e.target.value
        })
    }
    saveAirport=(e) =>{
        e.preventDefault()
        let airport ={airportCode:this.state.airportCode, airportName:this.state.airportName, airportLocation:this.state.airportLocation}
        if(airport.airportCode.length ===3 && airport.airportName!==''&&airport.airportLocation!==''){
            AirportService.createAirport(airport).then(()=>{
                this.props.history.push("/")
            }

            )
        } 
    }
    render() {
        return (
            <div className='contianer'>
                <div className='row'>
                    <div className='card col-md-6 offset-md-3 offset-md-3'>
                        <h3 className='text-center'>New Airport</h3>
                        <div className='card-body'>
                            <form>
                                <div className='form-group'>
                                    <label>Airport Name:</label>
                                    <input placeholder='Airport Name' name='airportName' className='form-control' value={this.state.airportName} onChange={this.changeAirportName}></input>
                                    <label>Airport Code:</label>
                                    <input placeholder='Airport Code' name='airportCode' className='form-control' value={this.state.airportCode} onChange={this.changeAirportCode}></input>
                                    <label>Airport Location:</label>
                                    <input placeholder='Airport Location' name='airportLocation' className='form-control' value={this.state.airportLocation} onChange={this.changeAirportLocation}></input>
                                </div>
                                <button className='btn btn-success' onClick={this.saveAirport}>Save Aiport </button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default CreateAirportComponent;
