import React from "react";
import { Link } from "react-router-dom";
import { useState } from "react";
import ReservationService from "../../services/ReservationService";

import SelectorComponent from "../helpers/SelectorComponent";

function ItineraryComponent(props, history) {
  const [flights] = useState(props.itinerary);
  // const navigate = useNavigate();
  let seatClass = ["First", "Business", "Economy"];
  function flightBooker() {
    let user = JSON.parse(sessionStorage.getItem("user"));

    flights.forEach((flight) => {
      ReservationService.createReservations(
        flight.id,
        user.id,
        localStorage.getItem(flight.id)
      ).then(() => {});
    });
    localStorage.setItem("booked", true);
  }
  function handleSelectChange(selectedValue) {
    console.log(selectedValue);
  }
  return (
    <div>
      <h2 className="text-center mt-3 ">Selected Itinerary</h2>
      <div className="row flight-list"> </div>
      <table className="table  table-striped flight-list mb-5">
        <thead>
          <tr>
            <th>Airline</th>
            <th>Departure Time</th>
            <th>Arrival Time</th>
            <th>Details</th>
          </tr>
        </thead>
        <tbody className="flight-list">
          {flights.map((flight) => {
            console.log(flight);
            let seatClassFlight = seatClass.filter((flight) => true);

            return (
              <tr key={flight.id}>
                <td>{flight.airline}</td>
                <td>
                  <small>
                    {new Date(flight.departureDate).toLocaleString()}
                  </small>
                </td>
                <td>
                  <small>{new Date(flight.arrivalDate).toLocaleString()}</small>
                </td>
                <td>
                  <div className="row">
                    <SelectorComponent
                      id={flight.id}
                      arrayOfData={seatClassFlight}
                      location={flight.id}
                      onSelectChange={handleSelectChange}
                    />
                    <div>{flight.air}</div>
                  </div>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>

      <div className="row">
        <button
          className="btn btn-dark col-2 offset-md-9 mb-2"
          onClick={flightBooker}
        >
          Book
        </button>
      </div>
    </div>
  );
}

export default ItineraryComponent;
