import React, { Component } from "react";
import ReservationService from "../../services/ReservationService";

class ReservationListComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      flightNum: null,
      reservations: [],
    };
  }
  componentDidMount() {
    if (this.state.flightNum) {
      this.getReservations(this.state.flightNum);
    }
  }
  getReservations(flightNum) {
    ReservationService.getReservationForFlight(flightNum).then((res) => {
      console.log(res);
    });
  }
  //univeral change text handler for changes in any of the inputs in the form. Used this to reate DRY code instead of repeetive change handlers
  changeTextHandler = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  render() {
    return (
      <div className="container">
        <div className="card m-5 p-3 bg-subtle shadow-lg">
          <h3 className=" text-center m-3 p-1">Flight Reservations</h3>
          <div className="row">
            <input
              id="fligthNum"
              placeholder=""
              name="flightNum"
              className="form-control form-control-small"
              value={this.state.flightNum}
              onChange={this.changeTextHandler}
              required
            ></input>
            <label for="firstnameInput" className="form-label">
              Flight Number
            </label>
          </div>
        </div>
      </div>
    );
  }
}

export default ReservationListComponent;
