import React, { Component } from "react";
import CustomerService from "../../services/CustomerService";
import ReservationService from "../../services/ReservationService";

class ReservationListComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      flightNum: "",
      reservations: [],
    };
    this.getReservations = this.getReservations.bind(this);
  }
  componentDidMount() {
    this.getReservations();
  }
  getReservations() {
    console.log(this.state.flightNum);
    let customers = CustomerService.getCustomers();
    ReservationService.getReservationForFlight(this.state.flightNum).then(
      (res) => {
        let reservations = res.data.map((reservation) => {
          customers.then((response) => {
            response.data.map((customer) =>
              customer.reservations.forEach((customerRes) => {
                if (customerRes.reservationId === reservation.reservationId) {
                  reservation["cust"] = customer;
                }
              })
            );
          });
          return reservation;
        });
        console.log(reservations);
        this.setState({
          reservations: res.data,
        });
      }
    );
  }
  //univeral change text handler for changes in any of the inputs in the form. Used this to reate DRY code instead of repeetive change handlers
  changeTextHandler = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  render() {
    let reservations = this.state.reservations;
    return (
      <div className="container">
        <div className="card m-5 p-3 bg-subtle shadow-lg">
          <h3 className=" text-center m-3 p-1">Flight Reservations</h3>
          <div className="row form-floating">
            <input
              id="fligthNum"
              placeholder=""
              name="flightNum"
              className="form-control form-control-small"
              value={this.state.flightNum}
              onChange={this.changeTextHandler}
              required
            ></input>
            <label htmlFor="firstnameInput" className="form-label">
              Flight Number
            </label>
            <button className="btn btn-personal" onClick={this.getReservations}>
              Search
            </button>
          </div>
          {
            /** if reservations are present it displauys a table with customers reservations*/ reservations.length >
            0 ? (
              <div className="row">
                <table className="table table-striped mb-5">
                  <thead>
                    <tr>
                      <th>Id</th>
                      <th>Name</th>
                      <th>Class</th>
                    </tr>
                  </thead>
                  <tbody>
                    {reservations.map((res) => {
                      return (
                        <tr key={res.reservationId}>
                          <td>{res.reservationId}</td>
                          <td>{`${res.customer.firstName || null} ${
                            res.customer.lastName
                          }`}</td>
                          <td>
                            {res.flight.destinationAirport[0].airportCode}
                          </td>
                          <td>
                            {new Date(res.flight.departureDate).toDateString()}
                          </td>
                          <td>
                            <button
                              onClick={this.cancelRes}
                              value={res.flight.id}
                            >
                              Cancel
                            </button>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            ) : (
              <div>false</div>
            )
          }
        </div>
      </div>
    );
  }
}

export default ReservationListComponent;
