import React, { Component } from "react";
import SelecterListComponent from "../helpers/SelecterListComonent";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { selectedValue: "Nothing selected" };
  }
  handleSelectChange = (selectedValue) => {
    this.setState({ selectedValue: selectedValue });
    localStorage.setItem("departureCity", selectedValue);
    console.log(localStorage);
  };
  render() {
    return (
      <div className="App">
        <p className="App-intro">
          <SelecterListComponent
            arrayOfData={this.props.arrayOfData}
            onSelectChange={this.handleSelectChange}
          />{" "}
          <br />
          <br />
          <div> Selected value: {this.state.selectedValue} </div>{" "}
        </p>{" "}
      </div>
    );
  }
}

export default App;
