import React from "react";
import { Link } from "react-router-dom";
import { useState } from "react";

function ItineraryComponent(props) {
  const [flights, setFlights] = useState(props.itinerary);
  console.log(props.itinerary);
  console.log(flights);
  return (
    <div className="container">
      <h2 className="text-center mt-3">Selected Itinerary</h2>
      <div className="row flight-list"></div>
      <table className="table  table-striped flight-list mb-5">
        <thead>
          <tr>
            <th>Airline</th>
            <th>Departure Time</th>
            <th>Arrival Time</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody className="flight-list">
          {flights.map((flight) => (
            <tr key={flight.id}>
              <td>{flight.airline}</td>
              <td>
                <small>{new Date(flight.departureDate).toLocaleString()}</small>
              </td>
              <td>
                <small>{new Date(flight.arrivalDate).toLocaleString()}</small>
              </td>
              <td>
                <Link className="btn btn-light col m-3" to={`/search`}>
                  View
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ItineraryComponent;
