import React, { Component } from "react";
import AirportService from "../../services/AirportService";

import SelecterListComponent from "../helpers/SelecterListComonent";

class FlightSearchComponent extends Component {
  constructor() {
    super();
    this.state = {
      airports: [],
      selectCity: "None",
      isChecked: false,
      checkedValue: "",
      selection: "Roundtrip",
      selectedDepartureAirport: "none selcted",
      selectedReturn: null,
    };
    this.checkBoxHandler = this.checkBoxHandler.bind(this);
    this.handleChange = this.handleChange(this);
  }
  componentDidMount() {
    AirportService.getAirports().then((res) => {
      this.setState({
        airports: res.data.sort(this.compare),
      });
    });
  }
  checkBoxHandler(e) {
    this.setState({
      isChecked: !this.state.isChecked,
      selection: e.target.value,
    });
  }
  handleSelectChange = (selectedValue) => {
    this.setState({ selectedValue: selectedValue });
  };
  compare(a, b) {
    if (a.airportLocation < b.airportLocation) {
      return -1;
    }
    if (a.airportLocation > b.airportLocation) {
      return 1;
    }
    return 0;
  }

  render() {
    let airports = this.state.airports;
    return (
      <div className="d-flex justify-content-center align-items-center reservation">
        <div className="row">
          <div className="card col-md-6 offet-md-3 offset-md-3">
            <h2 className="text-center">Book Flights</h2>
            <div className="card-body">
              <form>
                <div className="form-group">
                  <div className="row">
                    <div className="selections">
                      <input
                        type="checkbox"
                        value="Roundtrip"
                        onClick={this.checkBoxHandler}
                        disabled={this.state.isChecked}
                      />
                      Round Trip
                      <input
                        type="checkbox"
                        value="Oneway"
                        onClick={this.checkBoxHandler}
                        disabled={this.state.isChecked}
                      />
                      One-way
                      <input
                        type="checkbox"
                        value="Multicity"
                        onClick={this.checkBoxHandler}
                        disabled={this.state.isChecked}
                      />
                      Multi-City
                    </div>
                  </div>
                  <div className="row">
                    <label className="col">Origin:</label>
                    <label className="col">Destination:</label>
                  </div>
                  <div className="row sealct-container">
                    <SelecterListComponent
                      arrayOfData={this.state.airports}
                      onSelectChange={this.handleSelectChange}
                    />{" "}
                    <br />
                    <br />
                    <div> Selected value: {this.state.selectedValue} </div>{" "}
                    {/* <select
                      className="mdb-select md-form col"
                      searchable="Search here.."
                      onChange={this.handleChange}
                      placeholder="Search.."
                      name="selectDepartureAirport"
                    >
                      {airports.map((airport) => (
                        <option value={airport.airportCode} key={airport.id}>
                          {airport.airportLocation}({airport.airportCode})
                        </option>
                      ))}
                    </select>
                    <select
                      className="mdb-select md-form col"
                      onChange={this.handleChange}
                      placeholder="Search.."
                      name="selectDepartureAirport"
                    >
                      {airports.map((airport) => (
                        <option key={airport.code}>
                          {airport.airportLocation}({airport.airportCode})
                        </option>
                      ))}
                    </select> */}
                  </div>
                  <div className="row">
                    <div className="col">Depart:</div>

                    {this.state.selection === "Roundtrip" ? (
                      <div className="col">Return:</div>
                    ) : (
                      <div></div>
                    )}
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default FlightSearchComponent;
