import React, { Component } from "react";
import { Link } from "react-router-dom";
import AirportService from "../../services/AirportService";
import SelectorComponent from "../helpers/SelectorComponent";

class FlightSearchComponent extends Component {
  constructor() {
    super();
    this.state = {
      airports: [],
      selectCity: "None",
      isChecked: false,
      checkedValue: "",
      selection: "Roundtrip",
    };
    this.checkBoxHandler = this.checkBoxHandler.bind(this);
  }
  componentDidMount() {
    AirportService.getAirports().then((res) => {
      this.setState({
        airports: res.data.sort(this.compare),
      });
    });
    localStorage.removeItem("origin");
    localStorage.removeItem("destination");
    console.log(localStorage);
  }
  checkSignIn() {
    if (!localStorage.getItem("user")) {
      this.props.history.push("/");
    }
  }
  checkBoxHandler(e) {
    this.setState({
      isChecked: !this.state.isChecked,
      selection: e.target.value,
    });
    localStorage.setItem("tripType", this.state.selection);
    console.log(localStorage);
  }
  changeAirport() {
    console.log("hi");
  }
  compare(a, b) {
    if (a.airportLocation < b.airportLocation) {
      return -1;
    }
    if (a.airportLocation > b.airportLocation) {
      return 1;
    }
    return 0;
  }

  render() {
    this.checkSignIn();
    let airports = this.state.airports;
    return (
      <div className="d-flex justify-content-center align-items-center reservation">
        <div className="row">
          <div className="card">
            <h2 className="text-center">Book Flights</h2>
            <div className="card-body">
              <form>
                <div className="form-group">
                  <div className="row">
                    <div className="selections">
                      <input
                        type="checkbox"
                        value="Roundtrip"
                        checked={this.state.selection === "Roundtrip"}
                        onClick={this.checkBoxHandler}
                      />
                      Round Trip
                      <input
                        type="checkbox"
                        value="Oneway"
                        onClick={this.checkBoxHandler}
                        checked={this.state.selection === "Oneway"}
                      />
                      One-way
                      <input
                        type="checkbox"
                        value="Multicity"
                        onClick={this.checkBoxHandler}
                        checked={this.state.selection === "Multicity"}
                      />
                      Multi-City
                    </div>
                  </div>
                  <div className="row">
                    <label className="col">Origin:</label>
                    <label className="col">Destination:</label>
                  </div>
                  <div className="row">
                    <SelectorComponent
                      className="col"
                      arrayOfData={airports}
                      location={"origin"}
                      onClick={this.changeAirport}
                    />
                    <SelectorComponent
                      className="col"
                      arrayOfData={airports}
                      location={"destination"}
                    />
                  </div>
                  <div className="row">
                    <div className="col">Depart:</div>

                    {this.state.selection === "Roundtrip" ? (
                      <div className="col">Return:</div>
                    ) : (
                      <div></div>
                    )}
                  </div>
                </div>
                {/* ? */}
                <Link
                  to={
                    localStorage.getItem("origin") &&
                    localStorage.getItem("destination")
                      ? "/flights"
                      : "/search"
                  }
                  className="d-flex justify-content-center"
                >
                  Search Flights!
                </Link>
                {/* ) */}
                {/* : (
                  <div />
                )} */}
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default FlightSearchComponent;
