import { Button } from 'bootstrap';
import React, { Component } from 'react';

class SignInComponent extends Component {
    constructor(props) {
        super(props);
        this.state={
            username:"",
            password:""
        }
    }
    resetLogin(){
        this.setState({
            username:"",
            password:""
        })
    }
    changeTextHandler=(e)=>{
        console.log(e.target.name)
        console.log(this.state.password.length, this.state.username.length)
        this.setState({
            [e.target.name]:e.target.value
        })
    }

    render() {
        return (
            <div className='container'>
                <div className='row'>
                    <div className='card col-md-6 offset-md-3 offset-md-3'>
                        <h3 className='text/-center'> Login</h3>
                        <div className='card-body'>
                            <form>
                            <label>Username:</label>
                                <input placeholder='Username' name='username' className='form-control' value={this.state.username} onChange={this.changeTextHandler}></input>
                                <label>Password:</label>
                                <input placeholder='Password' name='password' className='form-control'value={this.state.password} onChange={this.changeTextHandler}></input>
                            <button className='btn btn-success' onClick={this.resetLogin} disabled={this.state.username.length===0&&this.state.password.length===0}>Login</button>
                            <button className='btn' onClick={this.resetLogin} disabled={this.state.username.length===0&&this.state.password.length===0}>Reset</button>
                            </form>
                        </div>
                    </div>
                </div>

            </div>
        );
    }
}

export default SignInComponent;
