import React, { Component } from "react";
import { Link } from "react-router-dom";
import AirportService from "../../services/AirportService";
import SelectorComponent from "../helpers/SelectorComponent";
import Calendar from "../helpers/Calendar";
import { CalendarComponent } from "@syncfusion/ej2-react-calendars";

class FlightSearchComponent extends Component {
  constructor() {
    super();
    this.state = {
      airports: [],
      selectCity: "None",
      isChecked: false,
      checkedValue: "",
      selection: "Roundtrip",
      minDate: new Date(),
    };
    this.checkBoxHandler = this.checkBoxHandler.bind(this);
    this.handleSelectChange = this.handleSelectChange.bind(this);
  }
  componentDidMount() {
    AirportService.getAirports().then((res) => {
      this.setState({
        airports: res.data.sort(this.compare),
      });
    });
    localStorage.removeItem("origin");
    localStorage.removeItem("destination");
  }
  checkSignIn() {
    if (sessionStorage.getItem("user") === null) {
      this.props.history.push("/");
    }
  }
  checkBoxHandler(e) {
    this.setState({
      isChecked: !this.state.isChecked,
      selection: e.target.value,
    });
    localStorage.setItem("tripType", this.state.selection);
  }
  changeAirport() {
    console.log("hi");
  }
  compare(a, b) {
    if (a.airportLocation < b.airportLocation) {
      return -1;
    }
    if (a.airportLocation > b.airportLocation) {
      return 1;
    }
    return 0;
  }
  handleSelectChange(selectedValue, location, event) {
    console.log(selectedValue, location, event.target);
    this.setState({
      location: selectedValue,
    });
  }

  render() {
    this.checkSignIn();
    let airports = this.state.airports;
    return (
      <div className="container">
        <div className="row m-5">
          <div className="card bg-subtle shadow-lg">
            <h2 className="text-center  mt-3">Book Flights</h2>
            <div className="card-body">
              <form>
                <div className="form-group ">
                  <div className="row">
                    <div className="form-check col-md-3">
                      <input
                        className="form-check-input"
                        type="checkbox"
                        value="Roundtrip"
                        checked={this.state.selection === "Roundtrip"}
                        onClick={this.checkBoxHandler}
                      />
                      <label className="form-check-label"> Round Trip</label>
                    </div>
                    <div className="form-check col-md-3">
                      <input
                        className="form-check-input"
                        type="checkbox"
                        value="Oneway"
                        onClick={this.checkBoxHandler}
                        checked={this.state.selection === "Oneway"}
                      />
                      One-way
                    </div>
                    <div className="form-check col-md-3">
                      <input
                        className="form-check-input"
                        type="checkbox"
                        value="Multicity"
                        onClick={this.checkBoxHandler}
                        checked={this.state.selection === "Multicity"}
                      />
                      Multi-City
                    </div>
                  </div>
                  <div className="row mt-2">
                    <label className="col-3">Origin:</label>
                    <label className="col-3 offset-md-3">Destination:</label>
                  </div>
                  <div className="row">
                    <SelectorComponent
                      className=""
                      arrayOfData={airports}
                      location={"origin"}
                      onSelectChange={this.handleSelectChange}
                    />
                    <SelectorComponent
                      className=""
                      arrayOfData={airports}
                      location={"destination"}
                      onSelectChange={this.handleSelectChange}
                    />
                  </div>
                  <div className="row mb-3 mt-2">
                    <div className="col col-md-3">
                      Depart:
                      <div className="row">
                        <CalendarComponent
                          id="calendar"
                          min={this.state.minDate}
                          start="Year"
                        />
                      </div>
                    </div>

                    {this.state.selection !== "Oneway" ? (
                      this.state.selection === "Roundtrip" ? (
                        <div className="col col-md-3 offset-md-2">
                          Return:
                          <div className="row">
                            <CalendarComponent
                              id="calendar"
                              min={this.state.minDate}
                              start="Year"
                            />
                          </div>
                        </div>
                      ) : (
                        <div className="col col-md-3 offset-md-2">
                          Depart:
                          <div className="row">
                            <CalendarComponent
                              id="calendar"
                              min={this.state.minDate}
                              start="Year"
                            />
                          </div>
                        </div>
                      )
                    ) : null}
                  </div>
                </div>
                {/* ? */}
                {
                  //still need to fix this its not working properly
                }
                <div className="row">
                  <Link
                    to={
                      localStorage.getItem("origin") &&
                      localStorage.getItem("destination") &&
                      localStorage.getItem("origin") !==
                        localStorage.getItem("destination")
                        ? "/flights"
                        : "/search"
                    }
                    className="btn btn-personal col-3 offset-md-8 mt-3 m-sm-5"
                  >
                    Search Flights!
                  </Link>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default FlightSearchComponent;
