import React, { Component } from "react";
import { Link } from "react-router-dom";
import FlightService from "../../services/FlightService";

class CreateFlightComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      airline: "",
      originCode: "",
      destinationCode: "",
      aircraft: "",
    };
    this.changeTextHandler = this.changeTextHandler.bind(this);
    this.saveFlight = this.saveFlight.bind(this);
  }
  //generica shange text for all inputs handler
  changeTextHandler = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  saveFlight(event) {
    event.preventDefault();

    let flight = {
      airline: this.state.airline,
      originCode: this.state.originCode,
      destinationCode: this.state.destinationCode,
      aircraft: this.state.aircraft,
    };
    FlightService.createFlight(flight).then(() => {
      this.props.history.push("/flights");
    });
  }
  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="card col-md-6 offset-md-3 offset-md-3">
            <h3 className="text-center">New Flight</h3>
            <div className="card-body">
              <form>
                <div className="form-group">
                  <label>Airline:</label>
                  <input
                    placeholder="Airline"
                    name="airline"
                    className="form-control"
                    value={this.state.airline}
                    onChange={this.changeTextHandler}
                  ></input>
                  <label>Origin:</label>
                  <input
                    placeholder="Origin"
                    name="origin"
                    className="form-control"
                    value={this.state.originCode}
                    onChange={this.changeTextHandler}
                  ></input>
                  <label>Destination:</label>
                  <input
                    placeholder="Destination"
                    name="destination"
                    className="form-control"
                    value={this.state.destinationCode}
                    onChange={this.changeTextHandler}
                  ></input>
                  <label>Aircraft:</label>
                  <input
                    placeholder="Aircraft"
                    name="aircraft"
                    className="form-control"
                    value={this.state.aircraft}
                    onChange={this.changeTextHandler}
                  ></input>
                </div>
                <button className="btn btn-success" onClick={this.saveFlight}>
                  Submit
                </button>
                <Link
                  className="btn btn-danger"
                  style={{ marginLeft: "10px" }}
                  to="/flights"
                >
                  Cancel
                </Link>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default CreateFlightComponent;
