import React, { Component } from 'react';
import FlightService from "../services/FlightService"
import { Link } from 'react-router-dom';

class ListFlightComponent extends Component {
    constructor(props){
        super(props)
        this.state={
            flights:[]
        }
    }
    componentDidMount(){
        FlightService.getFlights().then((res)=>{
            this.setState({
                flights:res.data
            })
        })
    }
    deleteCustomer(id){
        FlightService.deleteCustomer(id).then((res)=>{
            this.setState({
                flights:this.state.flights.filter((flight)=> flight.id!==id)
            })
        })
    }
    render() {
        let flights = this.state.flights
        return (
            <div>
                <h2 className='text-center'>Flight List</h2>
                <div className='row'><Link className="btn btn-primary"to="/add-flight">Add Flights</Link> </div>
                <div className='row'>
                    <table>
                        <thead>
                            <tr>
                                <th>Airline</th>
                                <th>Origin</th>
                                <th>Destination</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {flights.map(flight =>
                            <tr key={flight.id}>
                            <td>{flight.airline}</td>
                            <td>{flight.originCode}</td>
                            <td>{flight.destinationCode}</td>
                            <td>                                            <Link className='btn btn-info' props={{id : flight.id}}to={`/update-flight/${flight.id}`}>Update</Link>
                                            <button className='btn btn-danger' style={{marginLeft: "10px"}}onClick={()=>{this.deleteCustomer(flight.id)}}>Delete</button>
                                            <Link className='btn btn-info' props={{id : flight.id}}to={`/view-customer/${flight.id}`}>View</Link></td>
                            </tr>
                            )}
                        </tbody>
                    </table>
                </div>

            </div>
        );
    }
}

export default ListFlightComponent;
