import React, { Component } from "react";
import { Link } from "react-router-dom";
import AuthService from "../../services/AuthService";

class CreateCustomerComponent extends Component {
  constructor() {
    super();
    this.state = {
      firstName: "",
      middleName: "",
      lastName: "",
      email: "",
      address: "",
      phoneNumber: "",
      city: "",
      state: "",
      country: "",
      employee: false,
      password: "",
    };
    this.changeTextHandler = this.changeTextHandler.bind(this);
    this.saveCustomer = this.saveCustomer.bind(this);
  }
  changeTextHandler = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  saveCustomer = (e) => {
    e.preventDefault();
    let customer = {
      firstName: this.state.firstName,
      lastName: this.state.lastName,
      email: this.state.email,
      middleName: this.state.middleName,
      phoneNumber: this.state.phoneNumber,
      address: this.state.address,
      city: this.state.city,
      state: this.state.state,
      country: this.state.country,
      employee: this.state.employee,
      password: this.state.password,
    };
    if (
      customer.firstName !== "" &&
      customer.lastName !== "" &&
      customer.email !== ""
    ) {
      AuthService.signup(customer)
        .then(() => {
          console.log("Hi");
          this.props.history.push("/customers");
        })
        .catch((e) => {
          console.log(e);
        });
    }
  };

  render() {
    return (
      <div className="container">
        <div className="card m-4 bg-subtle shadow-lg">
          <h3 className="text-center mt-5">Sign Up</h3>
          <div className="card-body mb-4">
            <form className="mb-4">
              <div className="form-grid">
                <div className="row">
                  <div className="col-4">
                    <br></br>
                    <h5>Please Provide Your Name!</h5>
                    <p>As it is written on your government ID.</p>
                  </div>
                  <div className="col">
                    <label>First Name:</label>
                    <input
                      placeholder="First Name"
                      name="firstName"
                      className="form-control form-control-sm"
                      value={this.state.firstName}
                      onChange={this.changeTextHandler}
                    ></input>
                    <label>Middle Name:</label>
                    <input
                      placeholder="Middle Name"
                      name="middleName"
                      className="form-control form-control-sm"
                      value={this.state.middleName}
                      onChange={this.changeTextHandler}
                    ></input>
                    <label>Last Name:</label>
                    <input
                      placeholder="Last Name"
                      name="lastName"
                      className="form-control form-control-sm"
                      value={this.state.lastName}
                      onChange={this.changeTextHandler}
                    ></input>
                  </div>
                </div>
                <div className="row">
                  <h5 className="text-center">
                    Pleade provide your Contact information!
                  </h5>
                </div>
                <div className="row">
                  <div className="col">
                    <label>Email:</label>
                    <input
                      placeholder="Email"
                      name="email"
                      className="form-control"
                      value={this.state.email}
                      onChange={this.changeTextHandler}
                    ></input>
                    <label>Phone Number:</label>
                    <input
                      placeholder="Phone Number"
                      name="phoneNumber"
                      className="form-control"
                      value={this.state.phoneNumber}
                      onChange={this.changeTextHandler}
                    ></input>
                    <label>Password:</label>
                    <input
                      placeholder="Password"
                      name="password"
                      className="form-control"
                      value={this.state.password}
                      onChange={this.changeTextHandler}
                    ></input>
                  </div>{" "}
                  <div className="col">
                    <label>Address:</label>
                    <input
                      placeholder="Address"
                      name="address"
                      className="form-control"
                      value={this.state.address}
                      onChange={this.changeTextHandler}
                    ></input>
                    <label>City:</label>
                    <input
                      placeholder="City"
                      name="city"
                      className="form-control"
                      value={this.state.city}
                      onChange={this.changeTextHandler}
                    ></input>
                    <label>State:</label>
                    <input
                      placeholder="State"
                      name="state"
                      className="form-control"
                      value={this.state.state}
                      onChange={this.changeTextHandler}
                    ></input>
                    <label>Country:</label>
                    <input
                      placeholder="Country"
                      name="country"
                      className="form-control"
                      value={this.state.country}
                      onChange={this.changeTextHandler}
                    ></input>
                  </div>
                </div>
              </div>
              <button className="btn btn-success" onClick={this.saveCustomer}>
                Submit
              </button>
              <Link
                className="btn btn-danger"
                style={{ marginLeft: "10px" }}
                to="/customers"
              >
                Cancel
              </Link>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default CreateCustomerComponent;
