import React, { Component } from "react";
import { Link } from "react-router-dom";
import FlightService from "../../services/FlightService";
import SelectorComponent from "../helpers/SelectorComponent";
import AirportService from "../../services/AirportService";

class CreateFlightComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      airline: "",
      originCode: "",
      destinationCode: "",
      aircraft: "",
      airports: [],
      origin: "",
      destination: "",
    };
    this.changeTextHandler = this.changeTextHandler.bind(this);
    this.saveFlight = this.saveFlight.bind(this);
    this.handleSelectChange = this.handleSelectChange.bind(this);
  }
  compare(a, b) {
    if (a.airportLocation < b.airportLocation) {
      return -1;
    }
    if (a.airportLocation > b.airportLocation) {
      return 1;
    }
    return 0;
  }
  //generic shange text for all inputs handler
  componentDidMount() {
    AirportService.getAirports().then((res) => {
      this.setState({
        airports: res.data.sort(this.compare),
      });
    });
  }
  changeTextHandler = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  saveFlight(event) {
    event.preventDefault();

    let flight = {
      airline: this.state.airline,
      originCode: this.origin,
      destinationCode: this.destination,
      aircraft: this.state.aircraft,
    };
    FlightService.createFlight(flight).then(() => {
      this.props.history.push("/flights");
    });
  }
  handleSelectChange(selectedValue, location) {
    if (location === "origin") {
      this.setState({
        origin: selectedValue,
      });
    } else {
      this.setState({
        destination: selectedValue,
      });
    }
  }
  render() {
    let airports = this.state.airports;
    return (
      <div className="container">
        <div className="card card m-4  bg-subtle shadow-lg">
          <h3 className="text-center">New Flight</h3>
          <div className="card-body">
            <form>
              <div className="form-group">
                <label>Airline:</label>
                <input
                  placeholder="Airline"
                  name="airline"
                  className="form-control"
                  value={this.state.airline}
                  onChange={this.changeTextHandler}
                ></input>
                <label>Origin:</label>
                <SelectorComponent
                  className=""
                  arrayOfData={airports}
                  location={"origin"}
                  onSelectChange={this.handleSelectChange}
                />
                <label>Destination:</label>
                <SelectorComponent
                  className=""
                  arrayOfData={airports}
                  location={"destination"}
                  onSelectChange={this.handleSelectChange}
                />
                <label>Aircraft:</label>
                <input
                  placeholder="Aircraft"
                  name="aircraft"
                  className="form-control"
                  value={this.state.aircraft}
                  onChange={this.changeTextHandler}
                ></input>
              </div>
              <button className="btn btn-success" onClick={this.saveFlight}>
                Submit
              </button>
              <Link
                className="btn btn-danger"
                style={{ marginLeft: "10px" }}
                to="/flights"
              >
                Cancel
              </Link>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default CreateFlightComponent;
