import React from "react";
import { useHistory } from "react-router-dom";
import { useState } from "react";
import ReservationService from "../../services/ReservationService";

import SelectorComponent from "../helpers/SelectorComponent";

function ItineraryComponent(props) {
  const [flights] = useState(props.itinerary);
  const [prices, setPrices] = useState({});
  // const navigate = useNavigate();
  let seatClass = ["First", "Business", "Economy"];

  let history = useHistory();
  function flightBooker() {
    let user = JSON.parse(sessionStorage.getItem("user"));

    flights.forEach((flight) => {
      ReservationService.createReservations(
        flight.id,
        user.id,
        localStorage.getItem(flight.id)
      ).then(() => {});
    });
    localStorage.setItem("booked", true);
    console.log(sessionStorage);
    history.push(`/view-user/${sessionStorage.getItem("user")[0].id}`);
  }
  function handleSelectChange(selectedValue, e) {
    // setPrices({})
    let temp = {};
    if (selectedValue === "Business") {
      temp[e] = flights.filter((flight) => {
        return flight.id === e;
      })[0].aircraft.businessPrice;
    } else if (selectedValue === "First") {
      temp[e] = flights.filter((flight) => {
        return flight.id === e;
      })[0].aircraft.firstPrice;
    } else if (selectedValue === "Economy") {
      temp[e] = flights.filter((flight) => {
        return flight.id === e;
      })[0].aircraft.businessPrice;
    }
    console.log(temp);
    return setPrices({ ...prices }, temp);
  }
  return (
    <div>
      <h2 className="text-center mt-3 ">Selected Itinerary</h2>
      <div className="row flight-list"> </div>
      <table className="table  table-striped flight-list mb-5">
        <thead>
          <tr>
            <th>Airline</th>
            <th>Departure Time</th>
            <th>Arrival Time</th>
            <th>Details</th>
            <th>Ticket Price:</th>
          </tr>
        </thead>
        <tbody className="flight-list">
          {flights.map((flight) => {
            let seatClassFlight = seatClass.filter((seatClass) => {
              // console.log(seatClass);
              // console.log(flight);
              if (seatClass === "First") {
                // console.log
                // console.log(flight.tickets <= flight.aircraft.firstClass);
                return true;
              } else {
                return true;
              }
            });

            return (
              <tr key={flight.id}>
                <td>{flight.airline}</td>
                <td>
                  <small>
                    {new Date(flight.departureDate).toLocaleString()}
                  </small>
                </td>
                <td>
                  <small>{new Date(flight.arrivalDate).toLocaleString()}</small>
                </td>
                <td>
                  <div className="row">
                    <SelectorComponent
                      id={flight.id}
                      arrayOfData={seatClassFlight}
                      location={flight.id}
                      onSelectChange={handleSelectChange}
                      className="col"
                    />{" "}
                  </div>
                </td>

                {prices[flight.id] ? (
                  <td className="col">{prices[flight.id]}</td>
                ) : (
                  <td className="col"></td>
                )}
              </tr>
            );
          })}
        </tbody>
      </table>

      <div className="row">
        <button
          className="btn btn-dark col-2 offset-md-9 mb-2"
          onClick={flightBooker}
        >
          Book
        </button>
      </div>
    </div>
  );
}

export default ItineraryComponent;
