import React, { Component } from "react";
import FlightService from "../../services/FlightService";
import ReservationService from "../../services/ReservationService";

class ViewFlightComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: this.props.match.params.id || -1,
      flight: {},
      departureDate: "",
    };
    this.flightBooker = this.flightBooker.bind(this);
  }
  componentDidMount() {
    FlightService.getFlightById(this.state.id).then((res) => {
      this.setState({
        flight: res.data,
      });
    });
  }
  flightBooker() {
    let user = JSON.parse(sessionStorage.getItem("user"));

    ReservationService.createReservations(this.state.id, user.id).then(() => {
      this.props.history.push(`/view-customer/${user.id}`);
    });
  }
  checknextLeg() {
    if (localStorage.getItem("returnDate")) {
      let depDate = localStorage.getItem("returnDate");
      localStorage.setItem("departureDate", depDate);
      localStorage.removeItem("returnDate");
      this.setState({
        departureDate: localStorage.getItem("departureDate"),
      });
    } else if (this.state.departureDate.length > 0) {
      console.log("hi");
    }
  }
  render() {
    this.checknextLeg();
    console.log(localStorage);
    let flight = this.state.flight;
    let origin = "Not Available";
    let departureDate = "Not Available";
    let destination = "Not Available";
    let arrivalDate = "Not Available";
    if (flight.airline) {
      origin = flight.originAirport[0].airportName;
      departureDate = flight.departureDate;
      destination = flight.destinationAirport[0].airportName;
      arrivalDate = flight.arrivalDate;
    }
    return (
      <div className="card-body ">
        <h3 className="text-center">Flight Details</h3>
        <div className="card-body">
          <div className="row">
            <label>Airline:</label>
            <div>{flight.airline}</div>
            <label>Origin:</label>
            <div>{origin}</div>
            <label>Departure Time:</label>
            <div>{new Date(departureDate).toLocaleString()}</div>
            <label>Destination:</label>
            <div>{destination}</div>
            <label>Arrival Time:</label>
            <div>{new Date(arrivalDate).toLocaleString()}</div>
            <button className="btn" onClick={this.flightBooker}>
              Add to Itenerary
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default ViewFlightComponent;
